import React, { useState } from 'react';

const Login = (props) => {

    const [state, setState] = useState({
        view: "username",   // username or password
        username: "",
        authType: 1,        // 1=PASSWORD, 2=OTP
        password: "",
        otp: "",
        isUserEmpty: false,
        isPassEmpty: false,
        isOtpEmpty: false
    });

    const handleUserChange = (_e) => {
        let isEmpty = true;
        if (_e.target.value) {
            isEmpty = false;
        }
        setState((prevState) => ({ ...prevState, username: _e.target.value, isUserEmpty: isEmpty }));
    }

    const handlePasswordChange = (_e) => {
        let isEmpty = true;
        if (_e.target.value) {
            isEmpty = false;
        }
        setState((prevState) => ({ ...prevState, password: _e.target.value, isPassEmpty: isEmpty }));
    }

    const handleUserKeyDown = (_e) => {
        if (_e.key === 'Enter') {
            handleNextBtnClick(_e);
            return true;
        }
    }

    const handlePasswordKeyDown = (_e) => {
        if (_e.key === 'Enter') {
            handleSignInBtnClick();
            return true;
        }
    }

    const handleOtpChange = (_e) => {
        let isEmpty = true;
        if (_e.target.value) {
            isEmpty = false;
        }
        setState((prevState) => ({ ...prevState, otp: _e.target.value, isOtpEmpty: isEmpty }));
    }

    const handleNextBtnClick = (_e) => {

        if (!state.username) {
            setState((prevState) => ({ ...prevState, isValid: false }));
            return;
        }

        window._controller.dock(
            {
                method: "POST",
                endpoint: "/system/user/check",
                payload: { user: state.username }
            },
            (_req, _res) => {

                if (_res.status) {
                    setState((prevState) => ({ ...prevState, view: "authentication", authType: _res.payload.auth_type }));
                } else {
                    window._controller?.notify(_res.message, "error");
                }

            },
            (_req, _res) => {
                window._controller.notify(_res, "error");
            }
        );

        //setState((prevState) => ({...prevState, view: "authentication"}));
    }

    const handlePrevBtnClick = (_e) => {
        setState((prevState) => ({ ...prevState, view: "username" }));
    }

    const handleSignInBtnClick = () => {

        if (state.authType == 1 && state.isPassEmpty) {
            return;
        } else if (state.authType == 2 && state.isOtpEmpty) {
            return;
        }

        window._controller.dock(
            {
                method: "POST",
                endpoint: "/system/user/sign-in",
                payload: { user: state.username, password: state.password, otp: state.otp }
            },
            (_req, _res) => {

                if (_res.status) {

                    /* Successfully signed in */
                    sessionStorage.setItem("pharmarack_cms_user", _res.payload.user);
                    sessionStorage.setItem("pharmarack_cms_token", _res.payload.token);
                    // sessionStorage.setItem("pharmarack_cms_role", _res.payload.role.handle);
                    //sessionStorage.setItem("ff_menu", JSON.stringify(_res.payload.menu));                     

                    document.location.href = "";

                } else {
                    window._controller?.notify(_res.message, "error");
                }

            },
            (_req, _res) => {
                window._controller?.notify(_res, "error");
            }
        );

    }

    const handleForgotLinkClick = (_e) => {
        document.location.href = "?view=forgot-password";
    }

    const renderFormFields = () => {
        if (state.view === "username") {
            return (
                <>

                </>
            );
        } else {
            if (state.authType == 1) {
                return (
                    <>
                        <input type='password' placeholder='Enter Password' onKeyDown={handlePasswordKeyDown} onChange={handlePasswordChange} value={state.password} />
                        <p className={`pharmarack-cms-form-error-message ${state.isPassEmpty ? "error" : ""}`}>Please enter the password</p>

                    </>
                );
            } else if (state.authType == 2) {
                return (
                    <>
                        <input type='password' placeholder='Enter OTP' onChange={handleOtpChange} value={state.otp} />
                        <p className={`pharmarack-cms-form-error-message ${state.isOtpEmpty ? "error" : ""}`}>Please enter the received OTP</p>
                    </>
                );
            }
        }
    }

    const renderActions = () => {
        if (state.view === "username") {
            return <button className='pharmarack-cms-btn primary block icon-right' onClick={handleNextBtnClick}>Next <i className='far fa-caret-right'></i> </button>;
        } else {
            return (
                <>
                    <button className='pharmarack-cms-btn secondary block icon-left' onClick={handlePrevBtnClick}><i className='far fa-caret-left'></i> Back</button>
                    <button className='pharmarack-cms-btn primary block icon-right' onClick={handleSignInBtnClick}>Sign in <i className="far fa-right-to-bracket"></i></button>
                </>
            );
        }
    }

    return (

        <div className="pharmarack-cms-login-wrapper">

            <div className="pharmarack-cms-login-form">

                <div className="pharmarack-cms-login-form-header">
                    <img src="/assets/imgs/logo.svg" />
                    <p>Sign In to Continue</p>
                </div>

                <div className="pharmarack-cms-form-fields">
                    <div className="pharmarack-cms-form-row">
                        <input type='text' placeholder='Email or Mobile Number' onKeyDown={handleUserKeyDown} onChange={handleUserChange} value={state.username} />
                        <p className={`pharmarack-cms-form-error-message ${state.isUserEmpty ? "error" : ""}`}>Please enter you email or phone number</p>
                    </div>
                    <div className="pharmarack-cms-form-row">
                        <input type='password' placeholder='Password' onKeyDown={handlePasswordKeyDown} onChange={handlePasswordChange} value={state.password} />
                        <p className={`pharmarack-cms-form-error-message ${state.isPassEmpty ? "error" : ""}`}>Please enter the password</p>
                    </div>
                </div>

                <div className="pharmarack-cms-form-action">
                    <button className='pharmarack-cms-btn primary block icon-right' onClick={handleSignInBtnClick}>Sign in <i className="far fa-right-to-bracket"></i></button>
                    <a href="#" className='pharmarack-cms-forgot-link' onClick={handleForgotLinkClick}>Forgot Password</a>
                </div>

            </div>

        </div>

    )

}

export default Login;
